exemple.html:
/* Bon contraste */
.text-primary {
  color: #1a1a1a;
  background-color: #ffffff;
  /* Ratio: 15.6:1 */
}

/* Mauvais contraste */
.text-secondary {
  color: #666666;
  background-color: #ffffff;
  /* Ratio: 2.8:1 */
}

/* Focus visible */
button:focus {
  outline: 2px solid #007acc;
  outline-offset: 2px;
}

/* Ordre de tabulation logique */
<nav>
  <a href="#" tabindex="0">Accueil</a>
  <a href="#" tabindex="0">Services</a>
  <a href="#" tabindex="0">Contact</a>
</nav>
