codeExemple.nuxt:
// nuxt.config.ts
export default {
  build: {
    // Combiner les chunks
    optimization: {
      splitChunks: {
        chunks: 'all',
        cacheGroups: {
          styles: {
            name: 'styles',
            test: /\.(css|scss)$/,
            chunks: 'all',
            enforce: true
          }
        }
      }
    }
  }
}

