exemple.avant:
/* CSS non minifié */
.header {
  background-color: #ffffff;
  padding: 20px;
  margin-bottom: 10px;
}

.button {
  color: #000000;
  font-size: 16px;
  border: 1px solid #cccccc;
}

exemple.nginx:
# nginx.conf
location ~* \.(css|js|png|jpg|jpeg|gif|ico|svg)$ {
  expires 1y;
  add_header Cache-Control "public, immutable";
}
