exemple.html:
/* Bon contraste */
.text-primary {
  color: #1a1a1a;
  background-color: #ffffff;
  /* Ratio: 15.6:1 */
}

/* Mauvais contraste */
.text-secondary {
  color: #666666;
  background-color: #ffffff;
  /* Ratio: 2.8:1 */
}

/* Focus visible */
button:focus {
  outline: 2px solid #007acc;
  outline-offset: 2px;
}

/* Ordre de tabulation logique */
<nav>
  <a href="#" tabindex="0">Accueil</a>
  <a href="#" tabindex="0">Services</a>
  <a href="#" tabindex="0">Contact</a>
</nav>

exemple.css:
/* Animation normale */
@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

.element {
  animation: fadeIn 0.5s ease-in;
}

/* Respecter les préférences de mouvement réduit */
@media (prefers-reduced-motion: reduce) {
  .element {
    animation: none;
    opacity: 1;
  }

  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}
